package com.lyricsfrenzylite.android.utilities;

public class Lyric {

	
	private String lyric_body;
	
	private String lyric_lang;
	
	private String lyric_copy;
	
	private String track_url;

	
	public Lyric() {
		// TODO Auto-generated constructor stub
	}
	
	public String getLyric_body() {
		return lyric_body;
	}

	public void setLyric_body(String lyricBody) {
		lyric_body = lyricBody;
	}

	public String getLyric_lang() {
		return lyric_lang;
	}

	public void setLyric_lang(String lyricLang) {
		lyric_lang = lyricLang;
	}

	public String getLyric_copy() {
		return lyric_copy;
	}

	public void setLyric_copy(String lyricCopy) {
		lyric_copy = lyricCopy;
	}

	public String getTrack_url() {
		return track_url;
	}

	public void setTrack_url(String trackUrl) {
		track_url = trackUrl;
	}
	
}
